mapper.update:
update t_business set invoicestate = #{invoicestate},invoiceno = #{invoiceno},invoicecode=#{invoicecode},invoicetime=now()
where uuid in

  #{uuid}

mapper.select:
SELECT * from ( SELECT t.uuid as uuid,DATE_FORMAT(t.lastpaytime,'%Y-%m-%d %h:%i') as dateTime,t.hphm as hmp,t.payment as amount FROM t_business t INNER JOIN ( SELECT e.hphm, e.hpzl, d.mobilenum FROM account_vehicle e LEFT JOIN account d ON e.accountid = d.accountid WHERE
d.mobilenum = #{mobilenum, jdbcType=VARCHAR}
d.utoken = #{utoken,jdbcType=VARCHAR}
) f ON t.hphm = f.hphm AND t.hpzl = f.hpzl WHERE invoicestate = 0 and businessstate =2 ) r WHERE r.amount!=0 ORDER BY r.dateTime DESC

SELECT * from ( SELECT t.uuid as uuid,DATE_FORMAT(t.lastpaytime,'%Y-%m-%d %h:%i') as dateTime,t.hphm as hmp,t.payment-t.delayfee as amount,t.parkpointid FROM t_parkpot_business t INNER JOIN ( SELECT e.hphm, e.hpzl, d.mobilenum FROM account_vehicle e LEFT JOIN account d ON e.accountid = d.accountid WHERE
d.mobilenum = #{mobilenum, jdbcType=VARCHAR}
d.utoken = #{utoken,jdbcType=VARCHAR}
) f ON t.hphm = f.hphm AND t.hpzl = f.hpzl WHERE invoicestate = 0 and businessstate =2 ) r WHERE r.amount>0 ORDER BY r.dateTime DESC

SELECT tb.uuid, tb.hphm, tb.payment, tb.holdingtime, tb.parktime, tp.parkpointname as parkname FROM t_business tb INNER JOIN t_parkpoint tp ON tb.parkpointid = tp.parkpointid WHERE tb.uuid IN
#{uuid}
and tb.invoicestate = 0 AND tb.businessstate = 2
